CREATE OR REPLACE FUNCTION des_obter_valor_hora_relat ( nr_seq_cliente_p bigint) RETURNS bigint AS $body$
DECLARE


vl_hora_w	double precision := 140;

c01 CURSOR FOR
SELECT	vl_hora
from	des_regra_cobranca_rel_os
where (coalesce(nr_seq_cliente, nr_seq_cliente_p) = nr_seq_cliente_p)
and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_fim_vigencia,clock_timestamp())
order by coalesce(nr_seq_cliente,0);


BEGIN

if (nr_seq_cliente_p > 0) then
	open C01;
	loop
	fetch C01 into
		vl_hora_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		vl_hora_w := vl_hora_w;
		end;
	end loop;
	close C01;
else
	select	max(vl_hora)
	into STRICT	vl_hora_w
	from	des_regra_cobranca_rel_os
	where	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_fim_vigencia,clock_timestamp());
end if;

return	vl_hora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
