SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION diops_obter_dados_inconsist ( nr_seq_inconsistencia_p bigint, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE


/* ie_tipo_retorno_p
	C - Código
	D - Descrição
*/
ds_retorno_w			varchar(255);
cd_inconsistencia_w		smallint;
ds_inconsistencia_w		varchar(255);


BEGIN

select	cd_inconsistencia,
	ds_inconsistencia
into STRICT	cd_inconsistencia_w,
	ds_inconsistencia_w
from	diops_inconsistencia
where	nr_sequencia	= nr_seq_inconsistencia_p;

if (ie_tipo_retorno_p	= 'C') then
	ds_retorno_w		:= cd_inconsistencia_w;
elsif (ie_tipo_retorno_p	= 'D') then
	ds_retorno_w		:= ds_inconsistencia_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
