SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION diops_obter_dados_pj ( cd_ans_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
if (cd_ans_p IS NOT NULL AND cd_ans_p::text <> '') then
	if (ie_opcao_p = 'N') then
		select	max(nm_fantasia)
		into STRICT	ds_retorno_w
		from	pessoa_juridica
		where	cd_ans	= cd_ans_p;
	elsif (ie_opcao_p = 'C') then
		select	max(cd_cgc)
		into STRICT	ds_retorno_w
		from	pessoa_juridica
		where	cd_ans	= cd_ans_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
