CREATE OR REPLACE FUNCTION diops_obter_hashpwd ( nr_seq_operadora_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255)	:= '';
ds_senha_ans_w			varchar(20);


BEGIN

select	coalesce(max(ds_senha_ans),'')
into STRICT	ds_senha_ans_w
from	pls_outorgante
where	nr_sequencia	= nr_seq_operadora_p;

if (ds_senha_ans_w IS NOT NULL AND ds_senha_ans_w::text <> '') then
	ds_retorno_w	:= lower(obter_md5(ds_senha_ans_w));
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
