CREATE OR REPLACE FUNCTION diops_obter_inicio_cobertura (dt_mesano_referencia_p timestamp, dt_contratacao_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_dia_contratacao_w		varchar(255);
dt_dia_final_mes_ref_w		varchar(255);
dt_dia_w			varchar(255);


BEGIN

dt_dia_contratacao_w	:= to_char(dt_contratacao_p, 'dd');
dt_dia_final_mes_ref_w	:= to_char(fim_mes(dt_mesano_referencia_p), 'dd');

dt_dia_w		:= dt_dia_contratacao_w;
if ((dt_dia_contratacao_w)::numeric  > (dt_dia_final_mes_ref_w)::numeric ) then
	dt_dia_w	:= dt_dia_final_mes_ref_w;
end if;

return	to_date(dt_dia_w || '/' || to_char(dt_mesano_referencia_p, 'mm/yyyy'), 'dd/mm/yyyy');

end;
$body$
