CREATE OR REPLACE FUNCTION diops_obter_vl_saldo_periodo ( nr_seq_operadora_p bigint, nr_seq_periodo_p bigint, nr_seq_conta_lucprej_p bigint) RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
	DIOPS_FIN_GERAR_LUCRO_PREJ
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_conta_w			varchar(40);
ie_normal_encerramento_w	varchar(1);
vl_result_w			double precision;
vl_saldo_inicial_w		double precision;
vl_saldo_final_w		double precision;
ie_pos_mais_w			integer;
ie_pos_menos_w			integer;
ie_pos_w			integer;
dt_periodo_inicial_w		timestamp;
dt_periodo_final_w		timestamp;
dt_atual_w			timestamp;


BEGIN
begin /* Obter o período trimestral do DIOPS */
select	coalesce(a.dt_periodo_inicial, ''),
	coalesce(a.dt_periodo_final, ''),
	coalesce(a.ie_normal_encerramento, 'N')
into STRICT	dt_periodo_inicial_w,
	dt_periodo_final_w,
	ie_normal_encerramento_w
from	diops_periodo	a
where	a.nr_sequencia	= nr_seq_periodo_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(174234, 'NR_SEQ_OPERADORA=' || nr_seq_operadora_p || ';' ||
						'NR_SEQ_PERIODO=' || nr_seq_periodo_p);
end;

select	coalesce(max(a.cd_conta_contabil), '')
into STRICT	cd_conta_w
from	diops_trans_conta_lucprej	a
where	a.nr_sequencia	= nr_seq_conta_lucprej_p;

select	coalesce(sum(a.vl_saldo),0)
into STRICT	vl_saldo_inicial_w
from	ctb_mes_ref			b,
	ctb_balancete_v			a
where	a.nr_seq_mes_ref		= b.nr_sequencia
and	a.ie_normal_encerramento	= ie_normal_encerramento_w--'N'
and	b.dt_referencia	between pkg_date_utils.add_month(dt_periodo_inicial_w,-1,0) and fim_mes(pkg_date_utils.add_month(dt_periodo_inicial_w,-1,0))
and	a.cd_conta_contabil		= cd_conta_w;

/*Deve-se pegar apenas o ultimo saldo da conta contabil no balancete*/

select	coalesce(sum(a.vl_saldo),0)
into STRICT	vl_saldo_final_w
from	ctb_mes_ref			b,
	ctb_balancete_v			a
where	a.nr_seq_mes_ref		= b.nr_sequencia
and	a.ie_normal_encerramento	= ie_normal_encerramento_w--'N'
and	b.dt_referencia			= pkg_date_utils.start_of(dt_periodo_final_w, 'month')
and	a.cd_conta_contabil		= cd_conta_w;

select	vl_saldo_final_w - vl_saldo_inicial_w
into STRICT	vl_result_w
;

return vl_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION diops_obter_vl_saldo_periodo ( nr_seq_operadora_p bigint, nr_seq_periodo_p bigint, nr_seq_conta_lucprej_p bigint) FROM PUBLIC;

