CREATE OR REPLACE FUNCTION dis_obter_regra_local ( cd_material_p bigint, nr_seq_classif_p bigint, cd_local_estoque_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
result_w		varchar(1);
cd_setor_atendimento_w	integer;		
cd_grupo_material_w	smallint;
cd_subgrupo_w		smallint;
cd_classe_material_w	integer;
nr_seq_familia_w	bigint;
cd_local_estoque_w	bigint;
nr_seq_classif_w	bigint;


BEGIN 
 
select	cd_grupo_material, 
	cd_subgrupo_material, 
	cd_classe_material, 
	nr_seq_familia 
into STRICT	cd_grupo_material_w, 
	cd_subgrupo_w, 
	cd_classe_material_w, 
	nr_seq_familia_w 
from	estrutura_material_v 
where 	cd_material	= cd_material_p;
 
 
 
select 	count(*) 
into STRICT	result_w 
from 	dis_regra_local 
where	cd_estabelecimento						= cd_estabelecimento_p 
and	((coalesce(cd_grupo_material, cd_grupo_material_w)			= cd_grupo_material_w) or (coalesce(cd_grupo_material::text, '') = '')) 
and	((coalesce(cd_subgrupo_material, cd_subgrupo_w)			= cd_subgrupo_w) or (coalesce(cd_subgrupo_material::text, '') = '')) 
and	((coalesce(cd_classe_material, cd_classe_material_w)		= cd_classe_material_w) or (coalesce(cd_classe_material::text, '') = '')) 
and	((coalesce(cd_material, cd_material_p)				= cd_material_p) or (coalesce(cd_material::text, '') = '')) 
and	((coalesce(cd_setor_atendimento, cd_setor_atendimento_p)		= cd_setor_atendimento_p) or (coalesce(cd_setor_atendimento::text, '') = '')) 
and	((coalesce(nr_seq_classif, nr_seq_classif_p)				= nr_seq_classif_p) or (coalesce(nr_seq_classif::text, '') = '')) 
and	coalesce(cd_local_estoque, cd_local_estoque_p)			= cd_local_estoque_p 
and	((coalesce(nr_seq_familia, nr_seq_familia_w)			= nr_seq_familia_w) or (coalesce(nr_seq_familia::text, '') = '')) 
and	((coalesce(nr_seq_estrut_int::text, '') = '') or ((nr_seq_estrut_int IS NOT NULL AND nr_seq_estrut_int::text <> '') and consistir_se_mat_contr_estrut(nr_seq_estrut_int, cd_material_p) = 'S')) 
and	ie_gera_arquivo = 'S' 
order by 
	coalesce(cd_material,0), 
	coalesce(cd_classe_material,0), 
	coalesce(cd_subgrupo_material,0), 
	coalesce(cd_grupo_material,0), 
	coalesce(nr_seq_familia,0), 
	coalesce(cd_setor_atendimento,0);
 
if (coalesce(result_w,0) > 0) then 
	result_w := 'S';
else		 
	result_w := 'N';
	 
end if;	
 
 
return	result_w;
 
end;
