CREATE OR REPLACE FUNCTION dis_obter_regra_moviment ( ie_tipo_p text, cd_setor_origem_p bigint, cd_setor_destino_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
result_w	varchar(1);


BEGIN

if (ie_tipo_p = 'A') then
	select count(*)
	into STRICT	qt_existe_w
	from	dis_regra_movimentacao
	where 	ie_tipo_movimentacao = 'A'
	and 	cd_setor_origem = cd_setor_origem_p;

	if (coalesce(qt_existe_w,0) > 0) then
		result_w := 'S';
	else
		result_w := 'N';
	end if;
end if;

if (ie_tipo_p = 'T') then
	select count(*)
	into STRICT	qt_existe_w
	from	dis_regra_movimentacao
	where 	ie_tipo_movimentacao = 'T'
	and (coalesce(cd_setor_origem,0) = coalesce(cd_setor_origem_p,0) or coalesce(cd_setor_origem,0) = 0)
	and (coalesce(cd_setor_destino,0) = coalesce(cd_setor_destino_p,0) or coalesce(cd_setor_destino,0) = 0);

	if (coalesce(qt_existe_w,0) > 0) then
		result_w := 'S';
	else
		result_w := 'N';
	end if;
end if;

return	result_w;

end;
$body$
