SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION dl_obter_dados_distribuicao ( nr_seq_distribuicao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(255);

/*      ie_opcao_p
	A - nr adiantamento
	T - nr titulo pagar
*/
BEGIN
ds_retorno_w    := '';

if (ie_opcao_p = 'A') then
        select  max(nr_adiantamento)
        into STRICT    ds_retorno_w
        from    adiantamento_pago
        where   nr_seq_distribuicao    = nr_seq_distribuicao_p;
elsif (ie_opcao_p = 'T') then
        select  max(nr_titulo)
        into STRICT    ds_retorno_w
        from    titulo_pagar
        where   nr_seq_distribuicao    = nr_seq_distribuicao_p;
end if;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
