SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION dl_obter_msg_tp_distrib (ie_opcao_p numeric, vl_dominio_p numeric) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
1 - Regra de distribuição não cadastrada para o tipo
*/
ds_msg_w	varchar(255);
ds_retorno_w	varchar(255);


BEGIN

select	substr(b.ds_valor_dominio,1,50)
into STRICT	ds_msg_w
from	valor_dominio b,
	dominio a
where	a.cd_dominio	= 3131
and	a.cd_dominio	= b.cd_dominio
and	b.vl_dominio	= vl_dominio_p;

if (ie_opcao_p = 1) then
	ds_retorno_w	:= 'Regra de distribuição não cadastrada para o tipo ';
else
	ds_retorno_w	:= '';
end if;

return	ds_retorno_w || ds_msg_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
