SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION dmed_obter_dados_titular ( cd_titular_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
TC	- Telefone Celular	 (com DDD e máscara)
TF	- Telefone fixo	 (com DDD e máscara)
NM	- Nome pessoa
*/
ds_retorno_w	varchar(255):= '';


BEGIN
if (ie_opcao_p = 'TC') then
	begin
	select	nr_ddd_celular||nr_telefone_celular
	into STRICT	ds_retorno_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_titular_p;
	end;
elsif (ie_opcao_p = 'TF') then
	begin
	select	max(nr_ddd_telefone)||max(nr_telefone)
	into STRICT	ds_retorno_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica = cd_titular_p;
	end;
elsif (ie_opcao_p = 'NM') then
	begin
	select	nm_pessoa_fisica
	into STRICT	ds_retorno_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_titular_p;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION dmed_obter_dados_titular ( cd_titular_p text, ie_opcao_p text) FROM PUBLIC;

