CREATE OR REPLACE FUNCTION dmed_obter_grau_parente ( nr_titulo_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_relacao_w		varchar(2);


BEGIN
-- se n√£o tiver grau de parentesco insere '10' = outros
select	max(CASE WHEN g.ie_grau_parentesco='4' THEN  '03' WHEN g.ie_grau_parentesco='3' THEN  '04' WHEN g.ie_grau_parentesco='5' THEN  '08' WHEN g.ie_grau_parentesco='6' THEN  '08'  ELSE '10' END )
into STRICT	ie_relacao_w
FROM pls_segurado a
LEFT OUTER JOIN grau_parentesco g ON (a.nr_seq_parentesco = g.nr_sequencia)
WHERE a.nr_sequencia	in (SELECT	e.nr_seq_segurado
				from	titulo_receber			r,
					pls_mensalidade_segurado	e
				where	r.nr_seq_mensalidade	= e.nr_seq_mensalidade
				and	r.nr_titulo		= nr_titulo_p) and a.cd_pessoa_fisica	= cd_pessoa_fisica_p;

return ie_relacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
