CREATE OR REPLACE FUNCTION dmed_obter_valores_tit_resp ( nr_seq_caixa_receb_p bigint, cd_responsavel_p text) RETURNS bigint AS $body$
DECLARE

 
vl_titulos_w	double precision;			
 

BEGIN 
 
select	sum(coalesce(b.vl_recebido,0)) 
into STRICT	vl_titulos_w 
from	titulo_receber a, 
	titulo_receber_liq b, 
	atendimento_paciente c 
where	a.nr_titulo = b.nr_titulo 
and	a.nr_atendimento = c.nr_atendimento 
and	b.nr_seq_caixa_rec = nr_seq_caixa_receb_p;
/*and	c.cd_pessoa_fisica = cd_responsavel_p; */
 
 
 
return	vl_titulos_w;
 
end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
