SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION dsb_obter_acesso_ind ( nr_seq_indicador_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1)	:= 'S';
cd_setor_w		bigint;
cd_cargo_w		bigint;
cd_perfil_w		bigint;
cd_perfil_ativo_w		bigint;
nm_usuario_lib_w		varchar(15);
nr_seq_grupo_usuario_w	bigint;
qt_registro_w		bigint;

c01 CURSOR FOR
SELECT	coalesce(a.ie_permite,'N') ie_permite,
	a.cd_perfil,
	a.cd_setor_atendimento,
	a.nm_usuario_lib,
	a.nr_seq_grupo_usuario
from	dsb_lib_indicador a
where	nr_seq_indicador			= nr_seq_indicador_p
and	coalesce(cd_cargo, cd_cargo_w)		= cd_cargo_w
and	coalesce(cd_perfil, cd_perfil_ativo_w)	= cd_perfil_ativo_w
and	coalesce(nm_usuario_lib, nm_usuario_p)	= nm_usuario_p
order by	coalesce(nm_usuario_lib,'A'),
		coalesce(nr_seq_grupo_usuario,0),
	coalesce(cd_perfil,0),
	 coalesce(cd_cargo,0),
	 coalesce(cd_setor_atendimento,0);
	


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	dsb_lib_indicador
where	nr_seq_indicador = nr_seq_indicador_p;

begin
cd_perfil_ativo_w	:= coalesce(wheb_usuario_pck.get_cd_perfil,0);
exception when others then
	cd_perfil_ativo_w	:= 0;
end;

if (qt_registro_w > 0) then
	
	ie_permite_w	:= 'N';
	
	select	coalesce(max(b.cd_cargo),0)
	into STRICT	cd_cargo_w
	from	usuario a,
		pessoa_fisica b
	where	b.cd_pessoa_fisica	= a.cd_pessoa_fisica
	and	a.nm_usuario		= nm_usuario_p;
	
	open C01;
	loop
	fetch C01 into	
		ie_permite_w,
		cd_perfil_w,
		cd_setor_w,
		nm_usuario_lib_w,
		nr_seq_grupo_usuario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		if (coalesce(nm_usuario_lib_w::text, '') = '') and (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then
			
			select	coalesce(max('S'),'N')
			into STRICT	ie_permite_w
			from	usuario_perfil b
			where	b.cd_perfil	= cd_perfil_w
			and	b.nm_usuario	= nm_usuario_p
			and	ie_permite_w	= 'S';
			
		end if;
		
		if (coalesce(nm_usuario_lib_w::text, '') = '') and (cd_setor_w IS NOT NULL AND cd_setor_w::text <> '') then
			
			select	coalesce(max('S'),'N')
			into STRICT	ie_permite_w
			from	usuario_setor_v b
			where	b.cd_setor_atendimento	= cd_setor_w
			and	b.nm_usuario		= nm_usuario_p
			and	ie_permite_w		= 'S';
			
		end if;
		
		if (coalesce(nm_usuario_lib_w::text, '') = '') and (nr_seq_grupo_usuario_w IS NOT NULL AND nr_seq_grupo_usuario_w::text <> '') then
			
			select	coalesce(max('S'),'N')
			into STRICT	ie_permite_w
			from	usuario_grupo b
			where	b.nr_seq_grupo		= nr_seq_grupo_usuario_w
			and	b.nm_usuario_grupo	= nm_usuario_p
			and	ie_permite_w		= 'S';
		end if;
		
		ie_permite_w	:= ie_permite_w;
		end;
	end loop;
	close C01;
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
