SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION dsb_usuario_liberacao ( nr_seq_indicador_p bigint, nm_usuario_p text ) RETURNS varchar AS $body$
DECLARE

    ie_permite_w            varchar(1) := 'N';
    cd_setor_usuario_w		bigint;
    cd_cargo_usuario_w		bigint;
    cd_perfil_usuario_w		bigint;
    nm_usuario_lib_w		varchar(15);
    nr_seq_grupo_usuario_w	bigint;
    qt_registro_w           bigint;


BEGIN

    begin
        cd_perfil_usuario_w	:= coalesce(wheb_usuario_pck.get_cd_perfil,0);
        cd_setor_usuario_w    := coalesce(wheb_usuario_pck.get_cd_setor_atendimento,0);
        nm_usuario_lib_w      := nm_usuario_p;
    exception when others then
        cd_perfil_usuario_w	:= 0;
        cd_setor_usuario_w  := 0;
        nm_usuario_lib_w    := null;
    end;

    begin
        select	count(*)
        into STRICT	qt_registro_w
        from	dsb_lib_indicador
        where	nr_seq_indicador = nr_seq_indicador_p;
    end;



    if (qt_registro_w > 0) then
        begin                      
            select	coalesce(max(b.cd_cargo),0)
            into STRICT	cd_cargo_usuario_w
            from	usuario a,
                    pessoa_fisica b
            where	b.cd_pessoa_fisica	= a.cd_pessoa_fisica
            and	    a.nm_usuario		= nm_usuario_p;
        end;
        
            if (cd_perfil_usuario_w <> 0 and ie_permite_w <> 'S') then
                begin
                    select	coalesce(max(ie_permite),'N')
                    into STRICT    ie_permite_w
                    from	dsb_lib_indicador
                    where	nr_seq_indicador = nr_seq_indicador_p
                    and     ie_permite = 'S'
                    and     cd_perfil  = cd_perfil_usuario_w;
                end;
            end if;

            if (cd_setor_usuario_w <> 0 and ie_permite_w <> 'S') then
                begin
                    select	coalesce(max(ie_permite),'N')
                    into STRICT    ie_permite_w
                    from	dsb_lib_indicador
                    where	nr_seq_indicador = nr_seq_indicador_p
                    and     ie_permite = 'S'
                    and     cd_setor_atendimento  = cd_setor_usuario_w;
                end;
            end if;

            if ((nm_usuario_lib_w IS NOT NULL AND nm_usuario_lib_w::text <> '') and ie_permite_w <> 'S') then
                begin
                    select	coalesce(max(ie_permite),'N')
                    into STRICT    ie_permite_w
                    from	dsb_lib_indicador
                    where	nr_seq_indicador = nr_seq_indicador_p
                    and     ie_permite = 'S'
                    and     nm_usuario_lib  = nm_usuario_lib_w;
                end;
            end if;
            
           if (ie_permite_w <> 'S') then
                begin
                    select	coalesce(max(ie_permite),'N')
                    into STRICT    ie_permite_w
                    from	dsb_lib_indicador
                    where	nr_seq_indicador = nr_seq_indicador_p
                    and     ie_permite = 'S'
                    and     nr_seq_grupo_usuario in (
                    SELECT	nr_seq_grupo
                    from	USUARIO_GRUPO
                    where   nm_usuario_grupo = nm_usuario_p);
                end;
            end if;
        
        end if;

    return ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
