SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION duv_obter_inconsist_mensagens ( nr_seq_mensagem_p bigint, ie_tipo_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'X';

/*
ds_retorno_w - I >  Inconsistência na mensagem
ds_retorno_w - E >  Erro na mensagem


ie_tipo_p - Q > Verificar se existe algum registro de inconsistência
*/
BEGIN

if (coalesce(ie_tipo_p,'X') = 'X') then
	begin

	select	CASE WHEN count(*)=1 THEN  'E'  ELSE 'X' END
	into STRICT	ds_retorno_w
	from	duv_inconsistencia
	where	nr_seq_mensagem = nr_seq_mensagem_p
	and		(CD_ERRO IS NOT NULL AND CD_ERRO::text <> '');

	if (ds_retorno_w = 'X') then

		select	CASE WHEN count(*)=1 THEN  'I'  ELSE 'X' END
		into STRICT	ds_retorno_w
		from	duv_inconsistencia
		where	nr_seq_mensagem = nr_seq_mensagem_p
		and		(NR_SEQ_TIPO IS NOT NULL AND NR_SEQ_TIPO::text <> '');


	end if;

	end;
elsif (ie_tipo_p = 'Q') then
	begin

	select  CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT    ds_retorno_w
	from    duv_inconsistencia
	where   nr_seq_mensagem = nr_seq_mensagem_p;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
