SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION duv_obter_status_msg_seg (nr_seq_mensagem_p bigint, nr_seq_segmento_p bigint) RETURNS varchar AS $body$
DECLARE


/*
ds_retorno_w
A - Com alertas
E - Com erros
V - Válido

ie_nivel da tabela duv_msg_inconsistencia
-1             |Cancelado
0              |Válido
1              |Estrutura do arquivo
2              |Dados
3              |Aviso
*/
ds_retorno_w		varchar(1);
qt_w			bigint;
dt_consistencia_w	timestamp;


BEGIN

ds_retorno_w	:= null;

select	max(dt_consistencia)
into STRICT	dt_consistencia_w
from	duv_mensagem
where	nr_sequencia	= nr_seq_mensagem_p;

if (dt_consistencia_w IS NOT NULL AND dt_consistencia_w::text <> '') then
	if (nr_seq_segmento_p IS NOT NULL AND nr_seq_segmento_p::text <> '') then

		select	count(1)
		into STRICT	qt_w
		from	duv_msg_inconsistencia
		where	nr_seq_mensagem		= nr_seq_mensagem_p
		and	nr_seq_msg_segmento	= nr_seq_segmento_p
		and	ie_nivel		in ('1','2');

		if (qt_w > 0) then
			ds_retorno_w := 'E';
		else
			select	count(1)
			into STRICT	qt_w
			from	duv_msg_inconsistencia
			where	nr_seq_mensagem		= nr_seq_mensagem_p
			and	nr_seq_msg_segmento	= nr_seq_segmento_p
			and	ie_nivel		= '3';

			if (qt_w > 0) then
				ds_retorno_w := 'A';
			--else  comentei, pois não precisa mostrar o icone de validado no segmento, mostrar sim apenas se existir algum erro/alerta
			--	ds_retorno_w := 'V';
			end if;
		end if;
	else

		select	count(1)
		into STRICT	qt_w
		from	duv_msg_inconsistencia
		where	nr_seq_mensagem		= nr_seq_mensagem_p
		and	ie_nivel		in ('1','2');

		if (qt_w > 0) then
			ds_retorno_w := 'E';
		else
			select	count(1)
			into STRICT	qt_w
			from	duv_msg_inconsistencia
			where	nr_seq_mensagem		= nr_seq_mensagem_p
			and	ie_nivel		= '3';

			if (qt_w > 0) then
				ds_retorno_w := 'A';
			else
				ds_retorno_w := 'V';
			end if;
		end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
