SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION duv_obter_texto_html (line_p text) RETURNS varchar AS $body$
DECLARE


x varchar(32767) := null;
in_html boolean := FALSE;
s varchar(1);

BEGIN

if coalesce(line_p::text, '') = '' then
	return line_p;
end if;

for i in 1 .. length( line_p ) loop
	s := substr( line_p, i, 1 );
	if in_html then
		if s = '>' then
			in_html := FALSE;
		end if;
	elsif s = '<' then
		in_html := TRUE;
	end if;

	if not in_html and s != '>' then
		x := x || s;
	end if;
end loop;

return x;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
