CREATE OR REPLACE FUNCTION egk_get_person_sequence ( cd_pessoa_fisica_p pessoa_fisica_egk.cd_pessoa_fisica%type, ie_tipo_cartao_p pessoa_fisica_egk.ie_tipo_cartao%type default '1', ie_compara_p text default 'N') RETURNS bigint AS $body$
DECLARE


/*
ie_tipo_cartao_p
1 - EGK
2 - KVK

ie_compara_p
Must be removed in a refactoring
*/
nr_sequencia_w  pessoa_fisica_egk.nr_sequencia%type;
qt_cartoes_w    smallint;


BEGIN

nr_sequencia_w  := null;
qt_cartoes_w    := 0;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '')then

    if (ie_tipo_cartao_p = '1')then
        select  max(nr_sequencia)
        into STRICT    nr_sequencia_w
        from    pessoa_fisica_egk
        where   cd_pessoa_fisica = cd_pessoa_fisica_p
        and (coalesce(ds_conteudo_kvk::text, '') = '' or ie_tipo_cartao = '1');
    else
        select  max(nr_sequencia)
        into STRICT    nr_sequencia_w
        from    pessoa_fisica_egk
        where   cd_pessoa_fisica = cd_pessoa_fisica_p
        and ((ds_conteudo_kvk IS NOT NULL AND ds_conteudo_kvk::text <> '') or ie_tipo_cartao = '2');
    end if;

end if;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
