CREATE OR REPLACE FUNCTION ehr_apresentar_campo_se_vazio ( nr_seq_reg_template_p bigint, nr_seq_elemento_p bigint, ie_vazio_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1)	:= 'S';
ds_resultado_w	varchar(4000);
dt_liberacao_w	timestamp;


BEGIN

if (coalesce(ie_vazio_p::text, '') = '') or (ie_vazio_p = 'N') then

	begin
	select	dt_liberacao
	into STRICT	dt_liberacao_w
	from	ehr_reg_template
	where	nr_sequencia = nr_seq_reg_template_p;
	exception
	when others then
		ie_retorno_w	:= 'S';
	end;

	if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		begin
		select	coalesce(coalesce(ds_resultado,vl_resultado),dt_resultado)
		into STRICT	ds_resultado_w
		from	ehr_reg_elemento
		where	nr_seq_elemento = nr_seq_elemento_p
		and	nr_seq_reg_template = nr_seq_reg_template_p;
		exception
		when others then
			ds_resultado_w	:= null;
			ie_retorno_w	:= 'N';
		end;
	end	if;
end	if;

return	ie_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
