SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ehr_consistir_macro_mensagem ( ie_macro_consistencia_p text, ds_valor_p text, nr_atendimento_p bigint, ds_mascara_p text) RETURNS varchar AS $body$
DECLARE

dt_entrada_w	timestamp;
dt_valor_w	timestamp;
ie_retorno_w	varchar(1) := 'N';
						

BEGIN 
 
if (ie_macro_consistencia_p = 'DMEDE') then -- Data menor que data de entrada do paciente 
	select	dt_entrada 
	into STRICT	dt_entrada_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
		 
	select	to_date(ds_valor_p,ds_mascara_p) 
	into STRICT	dt_valor_w 
	;
	 
	if (dt_valor_w < dt_entrada_w) then		 
		ie_retorno_w	:= 'S';				
	end	if;	
end	if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
