CREATE OR REPLACE FUNCTION ehr_filtro_template ( nr_seq_template_p bigint, nr_seq_doenca_p bigint, nr_seq_especialidade_p bigint, ie_cluster_p text, ie_cluster_filtro_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(10)	:= 'S';
qt_reg_w			bigint;

BEGIN

if (nr_seq_doenca_p IS NOT NULL AND nr_seq_doenca_p::text <> '') then
	select	count(*)
	into STRICT	qt_reg_w
	from	ehr_template_doenca
	where	nr_seq_template = nr_seq_template_p
	and	nr_seq_doenca = nr_seq_doenca_p;

	if (qt_reg_w	= 0) then
		return	 'N';
	end if;
end if;


if (nr_seq_especialidade_p IS NOT NULL AND nr_seq_especialidade_p::text <> '') then
	select	count(*)
	into STRICT	qt_reg_w
	from	ehr_template_especialidade
	where	nr_seq_template = nr_seq_template_p
	and	nr_seq_especialidade = nr_seq_especialidade_p;

	if (qt_reg_w	= 0) then
		return	 'N';
	end if;
end if;


if (ie_cluster_filtro_p	<> 'A') and (ie_cluster_filtro_p IS NOT NULL AND ie_cluster_filtro_p::text <> '') and (ie_cluster_p	<> ie_cluster_filtro_p) then
	return 'N';
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
