CREATE OR REPLACE FUNCTION ehr_inf_resultado_exame (nr_seq_resultado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w   		varchar(20000);
dt_result_w           	varchar(30);
nm_exame_w            	varchar(255);
ds_resultado_w          varchar(1000);
ds_unidade_medida_w     varchar(15);

c01 CURSOR FOR

SELECT 	b.dt_resultado,
	obter_desc_exame(a.nr_seq_exame) nm_exame,
	obter_resultado_exame_ext(b.nr_seq_resultado,a.nr_sequencia)ds_resultado,
	l.ds_unidade_medida
from	exame_lab_resultado b
	left join exame_lab_result_item a  	on b.nr_seq_resultado 	= a.nr_seq_resultado
	left join exame_laboratorio e		on a.nr_seq_exame 	= e.nr_seq_exame
	left join lab_unidade_medida l   	on e.nr_seq_unid_med 	= l.nr_sequencia
where   b.nr_seq_resultado = nr_seq_resultado_p
order 	by 2;



BEGIN
open c01;
loop
fetch c01 into
    dt_result_w,
    nm_exame_w,
    ds_resultado_w,
    ds_unidade_medida_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
    begin
    if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
        ds_retorno_w := ds_retorno_w || chr(13) ||dt_result_w ||' - '||nm_exame_w||' - '||ds_resultado_w || '  ' ||ds_unidade_medida_w;
    else
        ds_retorno_w := dt_result_w ||' - '||nm_exame_w||' - '||ds_resultado_w || '  ' ||ds_unidade_medida_w;
    end if;

    end;
end loop;

close c01;

return substr(ds_retorno_w,1,2000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
