SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ehr_obter_geral ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1000);
ds_resultado_w		varchar(255);
nr_seq_elemento_w	bigint;
ie_primeiro_nulo_w	varchar(1);
qt_reg_w		integer;

C01 CURSOR FOR
	SELECT	nr_seq_elemento,
		ie_primeiro_nulo
	from	ehr_regra_elemento;


BEGIN
	open C01;
	loop
	fetch C01 into
		nr_seq_elemento_w,
		ie_primeiro_nulo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	if (ie_primeiro_nulo_w = 'S') then

		select	count(1)
		into STRICT	qt_reg_w
		from	ehr_registro b,
			ehr_reg_template a
		where	a.nr_seq_reg = b.nr_sequencia
		and	b.nr_atendimento = nr_atendimento_p
		and	exists (
		        SELECT	1
		        from	ehr_template_conteudo d
		        where	a.nr_seq_template = d.nr_seq_template
		        and	d.nr_seq_elemento = nr_seq_elemento_w);

		if (qt_reg_w > 0) then
			ds_resultado_w := 'S';
		else
			ds_resultado_w := null;
		end	if;
	end	if;

	ds_retorno_w := substr(ds_retorno_w || to_char(nr_seq_elemento_w) ||'='|| to_char(ds_resultado_w) ||'#@#@',1,255);

	end loop;
	close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
