CREATE OR REPLACE FUNCTION ehr_obter_informcao_entidade (nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_seq_template_p bigint, nr_seq_elemento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_empresa_w			smallint;
cd_estabelecimento_w		smallint;
cd_pessoa_fisica_w		varchar(10);
ds_retorno_w			varchar(2000)	:= '';
ie_origem_w			varchar(15);
ie_atendimento_w		varchar(1);

 
C01 CURSOR FOR 
	SELECT	d.ie_origem_inf, 
		coalesce(c.ie_atendimento,'S') 
	from	ehr_elemento d, 
		ehr_template_cont_inf c, 
		ehr_template_conteudo b, 
		ehr_template a 
	where	a.nr_sequencia		= nr_seq_template_p 
	and	a.nr_sequencia		= b.nr_seq_template 
	and	b.nr_sequencia		= c.nr_seq_temp_conteudo 
	and	b.nr_seq_elemento	= d.nr_sequencia 
	and	b.nr_seq_elemento	= nr_seq_elemento_p 
	and	((c.cd_estabelecimento	= cd_estabelecimento_w) or (coalesce(c.cd_estabelecimento::text, '') = '')) 
	and	(d.nr_seq_entidade IS NOT NULL AND d.nr_seq_entidade::text <> '') 
	and	(d.ie_origem_inf IS NOT NULL AND d.ie_origem_inf::text <> '');


BEGIN 
 
select	max(cd_pessoa_fisica), 
	max(cd_estabelecimento) 
into STRICT	cd_pessoa_fisica_w, 
	cd_estabelecimento_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
select	cd_empresa 
into STRICT	cd_empresa_w 
from	estabelecimento 
where	cd_estabelecimento	= cd_estabelecimento_w;
 
open	c01;
loop 
fetch	c01 into 
	ie_origem_w, 
	ie_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */	
	begin	 
	ie_origem_w		:= ie_origem_w;
	ie_atendimento_w	:= ie_atendimento_w;
	end;
end loop;
close c01;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
