SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ehr_obter_inf_elemento ( nr_seq_elemento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_origem_inf_w	varchar(15);
nr_seq_entidade_w	bigint;
ds_elemento_w	varchar(4000);
ie_componente_w	varchar(5);
nr_seq_tipo_dado_w	bigint;


BEGIN

select	ie_origem_inf,
	nr_seq_entidade,
	obter_desc_expressao(cd_exp_desc_elemento, ds_elemento),
	ie_componente,
	nr_seq_tipo_dado
into STRICT	ie_origem_inf_w,
	nr_seq_entidade_w,
	ds_elemento_w,
	ie_componente_w,
	nr_seq_tipo_dado_w
from	ehr_elemento
where	nr_sequencia = nr_seq_elemento_p;

if (ie_opcao_p	= 'IO') then
	return	ie_origem_inf_w;
elsif (ie_opcao_p	= 'EN') then
	return nr_seq_entidade_w;
elsif (ie_opcao_p	= 'DS') then
	return ds_elemento_w;
elsif (ie_opcao_p	= 'COMP') then
	return ie_componente_w;
elsif (ie_opcao_p	= 'TDADO') then
	return nr_seq_tipo_dado_w;
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
