CREATE OR REPLACE FUNCTION ehr_obter_reg_template ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_template_w	bigint;
nr_seq_reg_template_w	bigint;


BEGIN


select	max(nr_sequencia),
	max(nr_seq_template)
into STRICT	nr_seq_reg_template_w,
	nr_seq_template_w
from	ehr_reg_template
where	NR_SEQ_REG	= nr_sequencia_p
and	(NR_SEQ_TEMPLATE IS NOT NULL AND NR_SEQ_TEMPLATE::text <> '');

if (ie_opcao_p	 = 'R') then
	return nr_seq_reg_template_w;
elsif (ie_opcao_p	= 'T') then
	return nr_seq_template_w;
end if;



return	null;

end;
$body$
LANGUAGE PLPGSQL
