CREATE OR REPLACE FUNCTION ehr_obter_se_template_reg_lib ( nr_seq_tipo_reg_p bigint, nr_seq_template_p bigint) RETURNS varchar AS $body$
DECLARE

qt_reg_w	bigint;

BEGIN

if (coalesce(nr_seq_tipo_reg_p::text, '') = '') then
	return 'S';
end if;

if (nr_seq_tipo_reg_p IS NOT NULL AND nr_seq_tipo_reg_p::text <> '') and (nr_seq_template_p IS NOT NULL AND nr_seq_template_p::text <> '') then


	select	count(*)
	into STRICT	qt_reg_w
	from 	ehr_tipo_reg_item
	where	nr_seq_tipo_reg		= nr_seq_tipo_reg_p;

	if (qt_reg_w	> 0) then

		select	count(*)
		into STRICT	qt_reg_w
		from 	ehr_tipo_reg_item
		where	nr_seq_tipo_reg		= nr_seq_tipo_reg_p
		and	nr_seq_template		= nr_seq_template_p;

		if (qt_reg_w	= 0) then
			return 'N';
		end if;

	end if;
end if;

return	'S';

end;
$body$
LANGUAGE PLPGSQL
