CREATE OR REPLACE FUNCTION ehr_obter_se_texto_lib ( nr_seq_texto_p bigint, nr_seq_elemento_p bigint, nr_seq_template_p bigint, cd_perfil_p bigint, cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_regra_w		bigint;
ds_retorno_w		varchar(1) := 'S';
ie_liberar_w		varchar(1);

C01 CURSOR FOR 
	SELECT	ie_liberar 
	from	ehr_texto_padrao_lib 
	where	nr_seq_texto	= nr_seq_texto_p 
	and	coalesce(nr_seq_elemento,coalesce(nr_seq_elemento_p,0))	= coalesce(nr_seq_elemento_p,0) 
	and	coalesce(nr_seq_template,coalesce(nr_seq_template_p,0))	= coalesce(nr_seq_template_p,0) 
	and	coalesce(cd_perfil,coalesce(cd_perfil_p,0))		= coalesce(cd_perfil_p,0) 
	and	((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_pessoa_fisica_p,cd_especialidade) = 'S')) 
	order by coalesce(nr_seq_elemento,0), 
		coalesce(nr_seq_template,0), 
		coalesce(cd_especialidade,0), 
		coalesce(cd_perfil,0);


BEGIN 
 
select	count(*) 
into STRICT	qt_regra_w 
from	ehr_texto_padrao_lib 
where	nr_seq_texto	= nr_seq_texto_p;
 
if (qt_regra_w = 0) then 
	begin 
	ds_retorno_w	:= 'S';
	end;
else 
	begin 
	ds_retorno_w	:= 'N';
	open C01;
	loop 
	fetch C01 into	 
		ie_liberar_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		ds_retorno_w	:= ie_liberar_w;
		end;
	end loop;
	close C01;
	end;
end if;
 
return ds_retorno_w;
	 
end;
$body$
