CREATE OR REPLACE FUNCTION ehr_obter_texto_padrao ( nr_seq_texto_padrao_p bigint) RETURNS text AS $body$
DECLARE

retorno_w	text;

BEGIN
--deve ser passado por par√¢metro o valor NR_SEQ_TEXTO_PADRAO que esta na tabela ehr_template_conteudo
begin
	select 	ds_texto
	into STRICT 	retorno_w
	from   	ehr_texto_padrao
	where  	nr_sequencia = nr_seq_texto_padrao_p;
exception
	when no_data_found then
		retorno_w	:= null;
end;

return	retorno_w;

end;
$body$
