SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ehr_obter_tipo_comp_elem ( nr_seq_elemento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ie_componente_w		varchar(5);


BEGIN

if (nr_seq_elemento_p > 0) then
	begin
	select	ie_componente
	into STRICT	ie_componente_w
	from	ehr_elemento
	where	nr_sequencia	= nr_seq_elemento_p;

	ds_retorno_w	:= ie_componente_w;

	if (ie_opcao_p = 'D') then
		select	ds_valor_dominio
		into STRICT	ds_retorno_w
		from	valor_dominio
		where	cd_dominio	= 1043
		and	vl_dominio	= ie_componente_w;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
