SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ehr_obter_tipo_dado ( nr_seq_tipo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000);
ds_tipo_w	varchar(50);
ds_openehr_w	varchar(50);
ds_proposito_w	varchar(4000);
ds_uso_w	varchar(4000);

BEGIN

if (nr_seq_tipo_p > 0) then
	begin
	select	ds_tipo,
		ds_openehr,
		ds_proposito,
		ds_uso
	into STRICT	ds_tipo_w,
		ds_openehr_w,
		ds_proposito_w,
		ds_uso_w
	from	ehr_tipo_dado
	where	nr_sequencia	= nr_seq_tipo_p;

	if (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_tipo_w;
	elsif (ie_opcao_p = 'O') then
		ds_retorno_w	:= ds_openehr_w;
	elsif (ie_opcao_p = 'P') then
		ds_retorno_w	:= substr(ds_proposito_w,1,2000);
	elsif (ie_opcao_p = 'U') then
		ds_retorno_w	:= substr(ds_uso_w,1,2000);
	end if;
	end;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
