SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ehr_obter_tipo_elemento ( nr_seq_elemento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ie_tipo_w	varchar(15);


BEGIN

if (nr_seq_elemento_p > 0) then
	begin
	select	ie_tipo
	into STRICT	ie_tipo_w
	from	ehr_elemento
	where	nr_sequencia	= nr_seq_elemento_p;

	if (ie_opcao_p = 'D') then
		select	ds_valor_dominio
		into STRICT	ds_retorno_w
		from	valor_dominio
		where	cd_dominio	= 2394
		and	vl_dominio	= ie_tipo_w;
	elsif (ie_tipo_w = 'Data') then
		ds_retorno_w	:= '1';
	elsif (ie_tipo_w = 'State') then
		ds_retorno_w	:= '2';
	elsif (ie_tipo_w = obter_desc_expressao(737464)/*'History'*/
) then
		ds_retorno_w	:= '3';
	elsif (ie_tipo_w = obter_desc_expressao(7918006)/*'Protocol'*/
) then
		ds_retorno_w	:= '4';
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
