CREATE OR REPLACE FUNCTION ehr_obter_valor_item_regra ( nr_seq_regra_p bigint, cd_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(4000);


BEGIN

begin

select	CASE WHEN ie_regra_atualizacao='D' THEN ds_item WHEN ie_regra_atualizacao='C' THEN cd_item WHEN ie_regra_atualizacao='L' THEN ds_descricao_longa  ELSE '' END
into STRICT	ds_retorno_w
from	ehr_regra_template_item
where	nr_seq_regra = nr_seq_regra_p
and	cd_item	= cd_item_p;
exception
 when others then
  ds_retorno_w := '';
 end;

return ds_retorno_w;

end;
$body$
