CREATE OR REPLACE FUNCTION ehr_obter_valor_padrao_temp (nr_seq_template_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	bigint;
vl_padrao_w	varchar(4000);
ds_retorno_w	varchar(4000);

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		b.vl_padrao
	from	ehr_template_conteudo b,
		ehr_template a
	where	a.nr_sequencia	= nr_seq_template_p
	and	a.nr_sequencia	= b.nr_seq_template
	and	(b.vl_padrao IS NOT NULL AND b.vl_padrao::text <> '');

BEGIN

open	c01;
loop
fetch	c01 into
	nr_sequencia_w,
	vl_padrao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	/* Não foi colocado INSTR na query por questões de performance
	*/
	if (position('@SQL'  upper(vl_padrao_w)) = 0) then
		ds_retorno_w := ds_retorno_w || to_char(nr_sequencia_w) ||'='||to_char(vl_padrao_w) ||'#@#@';
	end	if;
	end;
end	loop;
close	c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
