CREATE OR REPLACE FUNCTION ehr_obter_val_padrao_temp_cont ( nr_seq_template_p bigint, nr_seq_conteudo_p bigint) RETURNS varchar AS $body$
DECLARE


vl_padrao_w	varchar(4000);

BEGIN
--nr_seq_template_p da tabela ehr_template e nr_seq_conteudo_p da tabela ehr_template_conteudo
begin
	select	b.vl_padrao
	into STRICT	vl_padrao_w
	from	ehr_template_conteudo b,
		ehr_template a
	where	a.nr_sequencia	= nr_seq_template_p
	and	a.nr_sequencia	= b.nr_seq_template
	and 	b.nr_sequencia	= nr_seq_conteudo_p
	and	(b.vl_padrao IS NOT NULL AND b.vl_padrao::text <> '');
exception
	when no_data_found then
		vl_padrao_w := null;
end;

return vl_padrao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
