CREATE OR REPLACE FUNCTION ehr_pep_pac_ci (nr_seq_reg_elemento_p bigint, nr_seq_entidade_p bigint, nr_seq_conteudo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_titulo_w		varchar(100);
ds_retorno_w	varchar(4000);

c01 CURSOR FOR 
SELECT 	substr(ds_titulo,1,101) ds_titulo 
from 	pep_pac_ci 
where	1 = 1 
and		nr_seq_reg_elemento = nr_seq_reg_elemento_p;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	ds_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
		if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then 
			ds_retorno_w := substr(ds_retorno_w ||chr(13) ,1,4000);
		end if;
 
	ds_retorno_w := substr(ds_retorno_w||ds_titulo_w,1,4000);
 
	end;
end loop;
close c01;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
