CREATE OR REPLACE FUNCTION ehr_vlr_clob (nr_seq_reg_template_p EHR_REG_ELEMENTO.NR_SEQ_REG_TEMPLATE%type, nr_seq_temp_conteudo_p EHR_REG_ELEMENTO.NR_SEQ_TEMP_CONTEUDO%type) RETURNS text AS $body$
DECLARE


clob_resultado_w ehr_reg_elemento_clob.clob_resultado%type;
nr_seq_reg_elemento_w EHR_REG_ELEMENTO_CLOB.NR_SEQ_REG_ELEMENTO%type;


BEGIN


select	coalesce(max(a.nr_sequencia), 0)
into STRICT nr_seq_reg_elemento_w
from	ehr_reg_elemento a,
	EHR_ELEMENTO b
where a.nr_seq_reg_template = nr_seq_reg_template_p
and	a.nr_seq_temp_conteudo = nr_seq_temp_conteudo_p
and a.NR_SEQ_ELEMENTO = b.nr_sequencia
and b.NR_SEQ_TIPO_DADO  = 15;


if (nr_seq_reg_elemento_w > 0) then

	select	c.clob_resultado
	into STRICT clob_resultado_w
	from EHR_REG_ELEMENTO_CLOB c
	where	c.NR_SEQ_REG_ELEMENTO = nr_seq_reg_elemento_w;

end if;

return clob_resultado_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
