CREATE OR REPLACE FUNCTION eis_nascimento_faixa_peso (qt_peso_p bigint) RETURNS varchar AS $body$
DECLARE


ds_peso_w	varchar(255);


BEGIN

if (qt_peso_p IS NOT NULL AND qt_peso_p::text <> '') then
	if (qt_peso_p < 500) then
		ds_peso_w := ' < 500 g';
	elsif (qt_peso_p between 500 and 749) then
		ds_peso_w := '500 - 749 g';
	elsif (qt_peso_p between 750 and 999) then
		ds_peso_w := '750 - 999 g';
	elsif (qt_peso_p between 1000 and 1249) then
		ds_peso_w := '1000 - 1249 g';
	elsif (qt_peso_p between 1250 and 1449) then
		ds_peso_w := '1250 - 1449 g';
	elsif (qt_peso_p between 1500 and 1749) then
		ds_peso_w := '1500 - 1749 g';
	elsif (qt_peso_p between 1750 and 1999) then
		ds_peso_w := '1750 - 1999 g';
	elsif (qt_peso_p between 2000 and 2249) then
		ds_peso_w := '2000 - 2249 g';
	elsif (qt_peso_p between 2250 and 2449) then
		ds_peso_w := '2250 - 2449 g';
	elsif (qt_peso_p > 2500) then
		ds_peso_w := '> 2500 g';
	end if;
end if;


return	ds_peso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
