CREATE OR REPLACE FUNCTION eis_obter_causa_indicador ( nr_seq_indicador_p bigint, nr_seq_causa_p bigint, dt_inicial_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_causa_w		bigint;
nr_seq_ind_dsb_w indicador_gestao.nr_sequencia%type;
ie_ativo_w constant varchar(2) := 'A';
nr_dias_w constant bigint := 180;

BEGIN

select  max(g.nr_sequencia)
into STRICT    nr_seq_ind_dsb_w 
from    ind_base b
inner join indicador_gestao g 
	on g.nr_seq_wheb = b.nr_seq_ind_gestao
where   b.nr_seq_ind_gestao = nr_seq_indicador_p
and     b.ie_situacao = ie_ativo_w;

select count(*)
into STRICT   qt_causa_w
from	qua_nao_conformidade a
inner join qua_nao_conform_causa b
	on b.nr_seq_nao_conform	= a.nr_sequencia
where (a.nr_seq_indicador	= nr_seq_ind_dsb_w
or     a.nr_seq_indicador = nr_seq_indicador_p)
and	   b.nr_seq_tipo_causa	= nr_seq_causa_p
and	   a.dt_abertura		>= coalesce(dt_inicial_p, clock_timestamp() - nr_dias_w);

return qt_causa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
