CREATE OR REPLACE FUNCTION eis_obter_dados_cirurgia ( nr_cirurgia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

ds_retorno_w	:= 'N';

if (ie_opcao_p = 'P') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from 	prescr_medica
	where 	nr_cirurgia 		= nr_cirurgia_p
	and	ie_tipo_prescr_cirur	= 2;

elsif (ie_opcao_p = 'A') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	cirurgia_agente_anestesico
	where	nr_cirurgia  = nr_cirurgia_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
