CREATE OR REPLACE FUNCTION eis_obter_meta_indicador (cd_estabelecimento_p bigint, nr_seq_indicador_p bigint, cd_ano_p bigint, cd_semestre_p text, ie_tipo_result_p text) RETURNS bigint AS $body$
DECLARE


/* Tipos de resultado

P = Valor padr√£o
B = BENCHMARKING

*/
vl_padrao_w		double precision	:= 0;
vl_BENCHMARKING_w	double precision	:= 0;
vl_retorno_w		double precision	:= 0;
nr_seq_indicador_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_indicador_w
from	indicador_gestao
where	nr_seq_wheb	= nr_seq_indicador_p;

select	coalesce(max(vl_padrao),0),
	coalesce(max(vl_BENCHMARKING),0)
into STRICT	vl_padrao_w,
	vl_BENCHMARKING_w
from	eis_padrao_indicador
where 	cd_estabelecimento	= cd_estabelecimento_p
and	nr_seq_indicador	= nr_seq_indicador_w
and	cd_ano			= cd_ano_p
and (cd_semestre		= cd_semestre_p or cd_semestre = 'A');

if (ie_tipo_result_p = 'P') then
	vl_retorno_w	:= vl_padrao_w;
elsif (ie_tipo_result_p = 'B') then
	vl_retorno_w	:= vl_BENCHMARKING_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
