CREATE OR REPLACE FUNCTION eis_obter_nfe_conta_estornada (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE
	

ie_cancelamento_w	conta_paciente.ie_cancelamento%type;
nr_sequencia_nf_w	nota_fiscal.nr_sequencia%type;
nr_nota_imp_w	        nota_fiscal.nr_nfe_imp%type;


BEGIN
        select  max(ie_cancelamento)
        into STRICT    ie_cancelamento_w
        from    conta_paciente
        where   nr_interno_conta = nr_interno_conta_p;	

	if (ie_cancelamento_w = 'E') then
		nr_sequencia_nf_w  := obter_nf_conta(nr_interno_conta_p, 6);
        elsif (ie_cancelamento_w = 'C') then
		nr_sequencia_nf_w  := obter_nf_conta(nr_interno_conta_p, 8);
        else
                nr_sequencia_nf_w  := obter_nf_conta(nr_interno_conta_p, 1);
	end if;

        if (nr_sequencia_nf_w = 0) then
                nr_nota_imp_w := null;
        else
                select	max(a.nr_nfe_imp)
                into STRICT    nr_nota_imp_w
                from	nota_fiscal a
                where	a.nr_sequencia = nr_sequencia_nf_w;
        end if;

return nr_nota_imp_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
