CREATE OR REPLACE FUNCTION eis_obter_ocup_censo ( dt_inicial_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint, ie_tipo_dado_p text default null) RETURNS bigint AS $body$
DECLARE


qt_leitos_w		bigint;
qt_leitos_ocupados_w	bigint;


BEGIN

/* Tipos de dados

OST - Ocupação sem temporários
OSTI - Ocupação sem temporários e interditados*/
if (coalesce(ie_tipo_dado_p::text, '') = '') then
	select	sum(nr_leitos_ocupados + nr_leitos_livres)
	into STRICT	qt_leitos_w
	from	eis_ocupacao_estabelecimento_v a
	where	dt_referencia between dt_inicial_p and dt_final_p
	and	obter_situacao_leito(cd_setor_atendimento, cd_unidade_basica, cd_unidade_compl) = 'A'
	and	ie_periodo = 'D'
	and	cd_estabelecimento = cd_estabelecimento_p
	and	ie_ocup_hospitalar = 'S';

	select	sum(nr_leitos_ocupados)
	into STRICT	qt_leitos_ocupados_w
	from	eis_ocupacao_estabelecimento_v a
	where	dt_referencia between dt_inicial_p and dt_final_p
	and	obter_situacao_leito(cd_setor_atendimento, cd_unidade_basica, cd_unidade_compl) = 'A'
	and	ie_periodo = 'D'
	and	cd_estabelecimento = cd_estabelecimento_p
	and	ie_ocup_hospitalar = 'S';

	return	dividir((qt_leitos_ocupados_w * 100),qt_leitos_w);
elsif (ie_tipo_dado_p = 'OST') then

	select	sum((nr_leitos_ocupados + nr_leitos_livres)- nr_unidades_interditadas)
	into STRICT	qt_leitos_w
	from	eis_ocupacao_estabelecimento_v a
	where	dt_referencia between dt_inicial_p and dt_final_p
	and	obter_situacao_leito(cd_setor_atendimento, cd_unidade_basica, cd_unidade_compl) = 'A'
	and	ie_periodo = 'D'
	and	cd_estabelecimento = cd_estabelecimento_p
	and	ie_ocup_hospitalar = 'S';

	select	sum(nr_leitos_ocupados)
	into STRICT	qt_leitos_ocupados_w
	from	eis_ocupacao_estabelecimento_v a
	where	dt_referencia between dt_inicial_p and dt_final_p
	and	obter_situacao_leito(cd_setor_atendimento, cd_unidade_basica, cd_unidade_compl) = 'A'
	and	ie_periodo = 'D'
	and	cd_estabelecimento = cd_estabelecimento_p
	and	ie_ocup_hospitalar = 'S';

	return	dividir((qt_leitos_ocupados_w * 100),qt_leitos_w);
elsif (ie_tipo_dado_p = 'OSTI') then

	select	sum((nr_leitos_ocupados + nr_leitos_livres)- (nr_unidades_interditadas + nr_unidades_temporarias))
	into STRICT	qt_leitos_w
	from	eis_ocupacao_estabelecimento_v a
	where	dt_referencia between dt_inicial_p and dt_final_p
	and	obter_situacao_leito(cd_setor_atendimento, cd_unidade_basica, cd_unidade_compl) = 'A'
	and	ie_periodo = 'D'
	and	cd_estabelecimento = cd_estabelecimento_p
	and	ie_ocup_hospitalar = 'S';

	select	sum(nr_leitos_ocupados)
	into STRICT	qt_leitos_ocupados_w
	from	eis_ocupacao_estabelecimento_v a
	where	dt_referencia between dt_inicial_p and dt_final_p
	and	obter_situacao_leito(cd_setor_atendimento, cd_unidade_basica, cd_unidade_compl) = 'A'
	and	ie_periodo = 'D'
	and	cd_estabelecimento = cd_estabelecimento_p
	and	ie_ocup_hospitalar = 'S';

	return	dividir((qt_leitos_ocupados_w * 100),qt_leitos_w);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
