SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION eis_obter_pernoite_autoriz ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_acompanhante_w	smallint;
nr_atendimento_w	bigint;
dt_entrada_w		timestamp;
dt_entrada_truncate_w		timestamp;
qt_visita_anterior_w	integer;
ds_pernoite_w		varchar(50);
ds_retorno_w		varchar(50);
ds_nao_saiu_mesmo_dia_W varchar(255);
ds_nao_saiu_W 	varchar(255);


BEGIN

select	eis_obter_se_visita_pernoite(nr_sequencia_p)
into STRICT	ds_pernoite_w
;

if (ds_pernoite_w = wheb_mensagem_pck.get_texto(795673)) or (ds_pernoite_w = wheb_mensagem_pck.get_texto(795671)) then

	select	coalesce(max(nr_atendimento),0)
	into STRICT	nr_atendimento_w
	from	atendimento_visita
	where	nr_sequencia	=	nr_sequencia_p;	

	select	coalesce(max(nr_acompanhante),0)
	into STRICT	nr_acompanhante_w
	from	atend_categoria_convenio
	where	nr_atendimento	=	nr_atendimento_w
	and	nr_seq_interno	=	obter_atecaco_atendimento(nr_atendimento);

	select	dt_entrada
	into STRICT	dt_entrada_w
	from	atendimento_visita
	where	nr_sequencia	=	nr_sequencia_p;
	
	dt_entrada_truncate_w := trunc(dt_entrada_w,'dd');
	ds_nao_saiu_mesmo_dia_W := substr(wheb_mensagem_pck.get_texto(795673),1,255);
	ds_nao_saiu_W := substr(wheb_mensagem_pck.get_texto(795671),1,255);

	select	count(*)
	into STRICT	qt_visita_anterior_w
	from	atendimento_visita
	where	nr_atendimento	=	nr_atendimento_w
	and	coalesce(ie_paciente,'N') = 'N'	
	and	dt_entrada	<	dt_entrada_w
	and	dt_entrada	>	dt_entrada_truncate_w
	and	eis_obter_se_visita_pernoite(nr_sequencia) in (ds_nao_saiu_mesmo_dia_W, ds_nao_saiu_W);

	if (nr_acompanhante_w = 0) then	
		ds_retorno_w := wheb_mensagem_pck.get_texto(795694);
	elsif (qt_visita_anterior_w >= nr_acompanhante_w) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(795698);
	else
		ds_retorno_w := wheb_mensagem_pck.get_texto(795699);
	end if;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
