SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION eis_obter_unidades_total ( dt_inicial_p timestamp, dt_final_p timestamp, cd_classif_setor_p text ) RETURNS bigint AS $body$
DECLARE


qt_total_w		integer;


BEGIN

if (coalesce(cd_classif_setor_p::text, '') = '') then
	select	SUM(obter_unidades_setor(cd_setor_atendimento))
	into STRICT	qt_total_w
	from 	setor_atendimento
	where		exists (	 SELECT	cd_setor_atendimento
				 from 	eis_ocupacao_setor_v a
				 where  	dt_referencia between dt_inicial_p and dt_final_p
				 and 	ie_periodo		= 'D'
				 and 	cd_estabelecimento		= 1
				 and 	a.ie_ocup_hospitalar	= 'S'
				 group  	by 1, cd_setor_atendimento, ds_setor_atendimento
				 having(AVG(12) > 0) and (SUM(nr_leitos_ocupados + nr_leitos_livres) > 0));
else
	select	SUM(obter_unidades_setor(cd_setor_atendimento))
	into STRICT	qt_total_w
	from 	setor_atendimento
	where	cd_classif_setor	= cd_classif_setor_p
	and	exists (	 SELECT	cd_setor_atendimento
				 from 	eis_ocupacao_setor_v a
				 where  	dt_referencia between dt_inicial_p and dt_final_p
				 and 	ie_periodo		= 'D'
				 and 	cd_estabelecimento		= 1
				 and 	a.ie_ocup_hospitalar	= 'S'
				 group  	by 1, cd_setor_atendimento, ds_setor_atendimento
				 having(AVG(12) > 0) and (SUM(nr_leitos_ocupados + nr_leitos_livres) > 0));
end if;

return qt_total_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
