CREATE OR REPLACE FUNCTION eis_regra_indicador_dimensao ( nr_seq_ind_gestao_p bigint, nr_seq_dimensao_p bigint, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'S';
ie_existe_regra_w	varchar(1);


BEGIN
ds_retorno_w	:= 'S';

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_existe_regra_w
from	indicador_regra a
where	coalesce(a.ie_situacao,'A') = 'A'
and	exists (SELECT 1 from indicador_regra_dimensao x where x.nr_seq_indicador = nr_seq_ind_gestao_p)
and	coalesce(a.nm_usuario_regra, nm_usuario_p) = nm_usuario_p
and	coalesce(a.cd_perfil, cd_perfil_p) = cd_perfil_p
and	coalesce(a.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p;

if (ie_existe_regra_w = 'S') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	indicador_regra_dimensao a
	where	a.nr_seq_indicador = nr_seq_ind_gestao_p
	and	a.nr_seq_dimensao = nr_seq_dimensao_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
