CREATE OR REPLACE FUNCTION eliminar_exp_inconven_mx ( ds_expressao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
