ds_retorno_w			doc_expressoes_inconven.ds_expressao_substituir%type;


BEGIN
if (ds_expressao_p IS NOT NULL AND ds_expressao_p::text <> '') then
	select	max(a.ds_expressao_substituir)
	into STRICT	ds_retorno_w
	from	doc_expressoes_inconven	a
	where	a.ds_expressao_inconven	= ds_expressao_p;

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= ds_expressao_p;
	end if;
end if;

return ds_retorno_w;

end;
