CREATE OR REPLACE FUNCTION elimina_acentos_longo ( ds_campo_p text, ie_remove_cedilha text default 'N') RETURNS varchar AS $body$
DECLARE


ds_campo_w  varchar(4000);
i     		integer;
k     		integer;
ds_acento_w	varchar(255)	:= 'áãàâäéêëèíóôõúüñÁÃÀÂÄÉÊËÈÍÓÔÕÚÜÑ';
ds_novo_w	varchar(255)	:= 'aaaaaeeeeiooouunAAAAAEEEEIOOOUUN';
ds_car_w	varchar(001);

BEGIN
ds_campo_w		:= '';
if	upper(ie_remove_cedilha) = 'S' then
	ds_acento_w	:= 'áãàâäéêëèíóôõúüñçÁÃÀÂÄÉÊËÈÍÓÔÕÚÜÑÇ';
	ds_novo_w	:= 'aaaaaeeeeiooouuncAAAAAEEEEIOOOUUNC';
end	if;
if (coalesce(ds_campo_p::text, '') = '') or (ds_campo_p = '') then
	ds_campo_w	:= ds_campo_p;
else
	begin
	FOR i IN 1..length(ds_campo_p) LOOP
 		begin
		ds_car_w	:= substr(ds_campo_p, i, 1);
		k := position(ds_car_w in ds_acento_w);
		if (k = 0) then
			ds_campo_w	:= ds_campo_w || ds_car_w;
		else
			ds_campo_w	:= ds_campo_w || substr(ds_novo_w,k,1);
		end if;
		end;
	END LOOP;
	end;
end if;

RETURN ds_Campo_w;

END;
$body$
